mapper.select:
SELECT * FROM ( SELECT work_name, work_detailed, UPLOAD_DATE, UPLOAD_NAME, views, VIEWS_COM FROM COMWORK WHERE COM_NAME = #{comName}
UNION ALL
SELECT work_name, work_detailed, UPLOAD_DATE, UPLOAD_NAME, views, VIEWS_COM FROM VIP_COMWORK WHERE COM_NAME = #{comName} ) WHERE ROWNUM <= 4

SELECT * FROM ( SELECT work_name, work_detailed, UPLOAD_DATE, views,VIEWS_COM, WORK_PERSON,WORK_FIELD,WORK_PAY WORK_PLAN,WORK_REGION,WORK_DATE,COM_NAME FROM COMWORK WHERE work_num = #{workid}
UNION ALL
SELECT work_name, work_detailed, UPLOAD_DATE, views,VIEWS_COM, WORK_PERSON,WORK_FIELD,WORK_PAY WORK_PLAN,WORK_REGION,WORK_DATE,COM_NAME FROM VIP_COMWORK WHERE work_num = #{workid} )
